CREATE PROCEDURE [dbo].[USPDeleteReview]
    @ReviewId                    INT
AS

SELECT @Total = COUNT(*) FROM [Author];
SELECT [Id] AS AuthorId, [FirstName], [LastName], [BooksCount] FROM ( SELECT DENSE_RANK() OVER(ORDER BY CASE WHEN @ColumName = 'FirstName' AND @DescendingOrder = 1 THEN [a].[FirstName] END DESC, CASE WHEN @ColumName = 'FirstName' THEN [a].[FirstName] END, CASE WHEN @ColumName = 'LastName' AND @DescendingOrder = 1 THEN [a].[LastName] END DESC, CASE WHEN @ColumName = 'LastName' THEN [a].[LastName] END, CASE WHEN @ColumName = 'BooksCount' AND @DescendingOrder = 1 THEN [a].[BooksCount] END DESC, CASE WHEN @ColumName = 'BooksCount' THEN [a].[BooksCount] END) AS [AuthorNum], *
FROM [Author] [a] ) t WHERE t.[AuthorNum] BETWEEN @Start * (@Length + 1) AND (@Start + 1) * @Length;

CREATE PROCEDURE [dbo].[USPGetAuthor]
    @AuthorId INT = NULL,
    @Start                     INT           = 1, 
    @Length                    INT           = 1000, 
    @Total                     INT OUTPUT, 
    @ColumName                 NVARCHAR(50)  = 'AuthorId', 
    @DescendingOrder           BIT           = 0
AS

BEGIN SELECT Id as BookId, * FROM Book WHERE ISNULL(@BookId, Id) = Id
DECLARE @Id INT;
DECLARE @CustomCursor CURSOR;
SET @CustomCursor = CURSOR FORWARD_ONLY FOR SELECT Id FROM Book WHERE ISNULL(@BookId, Id) = Id
OPEN @CustomCursor FETCH NEXT FROM @CustomCursor INTO @Id;
WHILE @@FETCH_STATUS = 0 BEGIN SELECT [a].Id AS AuthorId, [a].FirstName AS FirstName, [a].LastName AS LastName, [a].BooksCount AS BooksCount FROM Book [b] LEFT JOIN BookAuthor [ba] ON [b].Id = [ba].BookId LEFT JOIN Author [a] ON [a].Id = [ba].AuthorId WHERE [b].Id = @Id
FETCH NEXT FROM @CustomCursor INTO @Id; END END

